      marginTop: 10,
      padding: 0,
    },
    date: {
      color: 'gray',
      fontSize: 12.5,
    },
    postRow: {
      alignItems: 'center',
      flexDirection: 'row',
      paddingBottom: 6,
      paddingLeft: 15,
