    postImage: {
      backgroundColor: 'rgba(0, 0, 0, 0.075)',
      height: 500,
    },
    userImage: {
      marginRight: 12,
    },
    wordRow: {
      marginBottom: 10,
      paddingLeft: 15,
      paddingRight: 15,
      paddingTop: 6,
    },
    wordText: {
      fontSize: 14,
      fontWeight: '500',
      lineHeight: 22,
    },
 })

function ModifyPost (urlpost,description,userid) {

  swal({

    title: 'Modify Post:',
    text: "Description",
