      <Text style={styles.wordText}>{description}</Text>
      <Text style={styles.wordText}>{userid}</Text>
      <Text style={styles.wordText}>{date}</Text>
      <Button
                onPress={() => ModifyPost(urlpost,description,userid) }
                   title="Modify post"  
                />  
    </View>
  </View>
