import React, { useState, useRef, useEffect } from 'react';
import {SafeAreaView,View,Button,TextInput,Text,Alert,StyleSheet, FlatList } from 'react-native'
import AuthStyle from './AuthStyle'
import { connect } from 'react-redux';
const styles1 = StyleSheet.create({ ...AuthStyle})
import { SearchBar } from "react-native-elements";



function Home(props) {

    const [filtredData,setfilteredData] = useState([]);
    const [masterData,setmasterData] = useState([]);
    const [search, setsearch] = useState('')

    useEffect(() => {
        fetchUsers();
        return () =>{

        }
    },[])

    const fetchUsers = () => {
    const apiURL = 'https://jsonplaceholder.typicode.com/posts'
    fetch(apiURL)
    .then((response) => response.json())
    .then((responseJson) => {
        setfilteredData(responseJson)
        setmasterData(responseJson)
    }).catch((error) => {
        console.error(error)
    })
    }

    const searchFilter = (text) =>{
        setsearch(text);     
        if (text) {
            const newData = masterData.filter((item) => {
                const itemData = item.title ? item.title.toUpperCase()
                            :''.toUpperCase();
                const textData = text.toUpperCase();
                return itemData.indexOf(textData)> -1;
            });
            setfilteredData(newData);
        }
    }


    const ItemView=({item}) =>{
        return (
            <Text style={styles.itemStyle}>
                {item.id}{'. '}{item.title.toUpperCase()}
            </Text>
        )
    }

    const ItemSeparatorView = () => {
        return(
            <View
                style={{height : 0.5 , width:'100%' ,backgroundColor:'#c8c8c8'}}
            />
        )
    }


    return (
        <View style={styles.container}>

        <SearchBar
        onChangeText={(text) => searchFilter(text)}
        placeholder="Type username here..."
        placeholderTextColor="#888"
        cancelButtonTitle="Cancel"
        onCancel={() => console.log(onCancel())}
        value={search }
        inputContainerStyle={{backgroundColor: '95A472'}}
        leftIconContainerStyle={{backgroundColor: 'C8E087'}}
        inputStyle={{backgroundColor: '#1e3d59'}}

        />
        <FlatList
        data={filtredData}
        keyExtractor={(item,index) => index.toString()}
        ItemSeparatorComponent={ItemSeparatorView}
        renderItem={ItemView}
        /> 
        </View>
        /*<SafeAreaView style={{flex: 1}}>
             <View style={styles.container}>
                <TextInput
                    style={styles.textInputStyle}
                    value={search}
                    placeholder="Search with Pseudo"
                    underlineColorAndroid="transparant"
                    onChangeText={(text) => searchFilter(text)}
                />
                <FlatList
                    data={filtredData}
                    keyExtractor={(item,index) => index.toString()}
                    ItemSeparatorComponent={ItemSeparatorView}
                    renderItem={ItemView}
                />
             </View>
        </SafeAreaView>*/


        /*View style={styles.container}>
            <Text> {props.authToken}</Text>
            <Button
                onPress={() => props.navigation.navigate('Profile')} 
                title="Profil"  
            />  
             <Button
                onPress={() => props.navigation.navigate('LogIn')} 
                title="Retour"  
            />                          
        </View>*/

    );
}


const styles= StyleSheet.create({
    container:{
        backgroundColor:'white',
    },
    itemStyle:{
        padding:10
    },
    textInputStyle:{
        height: 50,
        borderWidth: 1,
        paddingLeft: 20,
        margin: 5,
        borderColor: '#009688',
        backgroundColor:'white'
      },

});

const mapStateToProps = state => {
    return {
        authToken: state.loginReducer.authToken,
    }
  }



export default connect(mapStateToProps)(Home);
