import React from 'react'
import {Image, Dimensions,Text, StyleSheet, View } from 'react-native'
import PropTypes from 'prop-types'
export const ImageProfil = require('./images/imagea.jpeg'); 
const styles = StyleSheet.create({

    container: {
      borderWidth: 0,
      justifyContent: 'space-between',
      marginBottom: 5,
      marginLeft: 12,
      marginRight: 12,
      marginTop: 10,
      padding: 0,
    },
    date: {
      color: 'gray',
      fontSize: 12.5,
    },
    postRow: {
      alignItems: 'center',
      flexDirection: 'row',
      paddingBottom: 6,
      paddingLeft: 15,
      paddingRight: 15,
      paddingTop: 6,
      width: Dimensions.get('window').width * 1,
    },
    postImage: {
      backgroundColor: 'rgba(0, 0, 0, 0.075)',
      height: 500,
    },
    userImage: {
      marginRight: 12,
    },
    wordRow: {
      marginBottom: 10,
      paddingLeft: 15,
      paddingRight: 15,
      paddingTop: 6,
    },
    wordText: {
      fontSize: 14,
      fontWeight: '500',
      lineHeight: 22,
    },
 })



const Post = ({
  containerStyle,
  urlpost,
  description

}) => {
  return (
    <View style={[styles.container, containerStyle]}>
    <View style={styles.postRow}>

    </View>

    {urlpost&& (
        <Image
          style={[
            styles.postImage

          ]}
          source={{uri: urlpost}} />)}
          <View style={styles.wordRow}>
      <Text style={styles.wordText}>{description}</Text>
    </View>
  </View>
  )
    /*<View style={[styles.container, containerStyle]}>
      { urlpost&& (
        <Image
          style={[
            styles.postImage,
            {
              width: 500*0.5,
              height: 500 * (500/ 500)*0.5,
            },
          ]}
          source={{uri: urlpost}}
        />
      )}
    </View>*/

}

Post.propTypes = {
  containerStyle: PropTypes.oneOfType([PropTypes.object, PropTypes.number]),
  urlpost: PropTypes.string,
  description: PropTypes.string,

}

Post.defaultProps = {
  containerStyle: {},
  urlpost: null,
  description: null,

}

export default Post
